import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Counter from "./Components/counter"; // Assuming Counter is a connected component

function App() {
    const dispatch = useDispatch();
    const count = useSelector(state => state.count); // Assuming your state has a count property

    return (
        <>
            <div className="bg-[blue] flex justify-around items-center gap-4 flex-col border border-black p-1">
                <button
                    onClick={() => dispatch({ type: 'INCREMENT' })}
                    className="w-[200px] p-3 bg-[green] border rounded-md text-white"
                >
                    Incre
                </button>

                <h1 className="text-center font-extralight text-[40px] text-white">{count}</h1>

                <button
                    onClick={() => dispatch({ type: 'DECREMENT' })}
                    className="w-[200px] p-3 bg-[red] border-r-2 text-white border rounded-md"
                >
                    Decre
                </button>
            </div>
        </>
    );
}

export default App;
