import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { addItem } from '../redux/Slices/Cartslice';

const Home = () => {
    // State to store fetched products
    const [products, setProducts] = useState([]);

    // Fetch data using useEffect
    useEffect(() => {
        fetch("https://dummyjson.com/carts")
            .then(res => res.json())
            .then(data => {
                console.log(data); // Check the fetched data
                setProducts(data.carts); // Assuming 'carts' is the array of products in the response
            })
            .catch(error => console.error("Error fetching products:", error));
    }, []); // Empty dependency array means this will run only once after the component mounts

    const dispatch = useDispatch();
    const items = useSelector(state => state.cart); // Assuming your cart slice is in `state.cart`
    const total = items.reduce((a, b) => a + b.price, 0);

    return (
        <>
            <div className="bg-gray-100 p-4">
                <h5 className='text-center text-gray-800 text-2xl mb-2'>Total: ${total}</h5>
                <h1 className='text-center text-gray-800 text-3xl'>Added: {items.length}</h1>
            </div>

            <div className='bg-gray-300 flex justify-around flex-wrap gap-6 p-4'>
                {Array.isArray(products) && products.map((cart, index) => (
                    <div key={index} className='min-w-[300px] h-[300px] flex gap-2 items-center justify-around flex-col bg-white rounded-lg shadow-lg transition-transform transform hover:scale-105'>
                        {/* <span className='font-bold text-lg text-gray-800'>Cart {index + 1}</span> */}
                        <div>
                            {cart.products.map((item, idx) => (
                                <div  className="flex flex-col items-center mb-2">
                                    <span className='font-bold text-gray-800'>{item.title}</span>
                                    <span className='text-gray-600'>Price: ${item.price}</span>
                                    <span className='text-red-500'>Discounted Total: ${item.discountedTotal}</span>
                                    <button
                                        onClick={() => dispatch(addItem({ title: item.title, price: item.price }))}
                                        className='p-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors'>
                                        Add to Cart
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </>
    );
};

export default Home;
