import { createStore } from "redux"; 

// Reducer function
const reducer = (state = 1, action) => {  
    switch (action.type) {
        case "INCREMENT":
            return state + 1;
        case "DECREMENT":
            return state - 1;
        case "MULTIPLY":
            return state * 2;
        default:
            return state; 
    }
};

export const store = createStore(reducer);
