import React from 'react';
import { useDispatch } from 'react-redux';
import Counter from "./Components/counter"; 

function App() {
    const dispatch = useDispatch();

    const handleIncrement = () => {
        dispatch({ type: 'INCREMENT' });
    };

    const handleDecrement = () => {
        dispatch({ type: 'DECREMENT' });
    };

    return (
        <>
            <div className="flex justify-around h-[100vh] items-center gap-4 flex-col border border-black p-1">
                <button 
                    onClick={handleIncrement}
                    className="w-[200px] p-3 bg-green-600 hover:bg-green-700 border rounded-md text-white"
                    aria-label="Increment counter"
                >
                    Increase
                </button>   

                <Counter/>  

                <button 
                    onClick={handleDecrement}
                    className="w-[200px] p-3 bg-red-600 hover:bg-red-700 text-white border rounded-md"
                    aria-label="Decrease counter"
                >
                    Decrease
                </button>
            </div> 
        </>
    );
}

export default App;
