import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Counter from "./Components/counter";

function App() {
    const dispatch = useDispatch();

    return (
        <>
            <div className="bg-[blue] flex justify-around items-center gap-4 flex-col border border-black p-1">
                <button
                    onClick={() => dispatch({ type: 'INCREMENT' })}
                    className="w-[200px] p-3 bg-[green] border rounded-md text-white"
                >
                    Incre
                </button>

                <Counter /> {/* This will display the current count */}

                <button
                    onClick={() => dispatch({ type: 'DECREMENT' })}
                    className="w-[200px] p-3 bg-[red] border-r-2 text-white border rounded-md"
                >
                    Decre
                </button>
            </div>
        </>
    );
}
