import React from "react";
import { createBrowserRouter, NavLink, RouterProvider } from "react-router-dom";
import Home from "./Components/Home";
import About from "./Components/About";
import Services from "./Components/Services";
// import Contact from "./Components/Contact"; 

const App = () => {
    const router = createBrowserRouter([
        {
            path: '/',
            element: <Home />,
        },
        {
            path: '/about',
            element: <About />,
        },
        // {
        //     path: '/contact', 
        //     element: <Contact />,
        // },
        {
            path: '/services',
            element: <Services />,
        },
    ]);

    return (
        <>
            <nav className="bg-blue-500 flex justify-between p-1">
                <h1 className="text-white font-medium">LOGO</h1>

                <ul className="flex gap-12 pr-2">
                    <li>
                        <NavLink 
                            to="/" 
                            className="text-[20px] font-medium text-white" 
                            activeClassName="text-yellow-500"
                        >
                            Home
                        </NavLink>
                    </li>
                    <li>
                        <NavLink 
                            to="/about" 
                            className="text-[20px] font-medium text-white" 
                            activeClassName="text-yellow-500"
                        >
                            About
                        </NavLink>
                    </li>
                    {/* <li>
                        <NavLink 
                            to="/contact" 
                            className="text-[20px] font-medium text-white" 
                            activeClassName="text-yellow-500"
                        >
                            Contact
                        </NavLink>
                    </li> */}
                    <li>
                        <NavLink 
                            to="/services" 
                            className="text-[20px] font-medium text-white" 
                            activeClassName="text-yellow-500"
                        >
                            Services
                        </NavLink>
                    </li>
                </ul>
            </nav>

            <RouterProvider router={router} />
        </>
    );
};

export default App;






















































