import About from "./Components/About";
import Services from "./Components/Services";
import Contact from "./Components/Contacts";

const App = () => {

    const router = createBrowserRouter ([
        {
            path:'/',
            element:<Home/>,
        },
        {
            path:'/about',
            element:<About/>,
        },
        {
            path:'/',
            element:<Contact/>
        },
        {
            path:'/services',
            element:<Services/>,
        },

    ])



