import { createStore } from "redux";

// Define the reducer
const reducer = (state = 0, action) => {
    switch (action.type) {
        case 'INCREMENT':
            return state + 1;
        case 'DECREMENT':
            return state - 1;
        default:
            return state;
    }
};

// Create the store and export it
export const store = createStore(reducer); // Named export
