import React from 'react';
import { Data } from './Components/Data';
import { useSelector } from "react-redux"
import { useDispatch } from 'react-redux';
import { addItem } from './redux/Slices/Cartslice';

function App() {
    const dispatch = useDispatch()
    const products = Data();
    const items = useSelector(state=>state)
    console.log(items);

    return (
        <>
            <div>
                <h1 className='text-center  text-black text-[50px]'>CARTS</h1>
            </div>

        <div className='bg-gray-400 flex justify-around flex-wrap gap-3 p-2'>

            {Array.isArray(products) && products.map((item, index) => (
                <div key={index} className=' w-[300px] h-[300px] flex gap-2 items-center justify-around flex-col bg-white  rounded shadow'>
                    <span className='font-bold'>{item.title}</span>
                    <span className='text-gray-700'>${item.price.toFixed(1)}</span>
                    <span className='text-red-500'>Discounted Total: ${item.discountedTotal}</span>
                    <button onClick={e => dispatch(addItem({}))} className='p-3 bg-[blue] text-[white] rounded-md'>Add to Cart</button>
                </div>
            ))}
        </div>
</>
    );
}

export default App;
